import { useState } from "react";

import Card from "../UI/Card";
import Button from "../UI/Button";
import classes from "./AddUser.module.css";

function AddUser(props) {
  const [addUsername, setAddUsername] = useState("");
  const [addAge, setAddAge] = useState("");
  const [isValid, setIsValid] = useState(true);

  function addUserFormHandler(event) {
    event.preventDefault();

    if (addUsername.trim().length === 0 || addAge.trim().length === 0) {
      setIsValid(false);
      return;
    }

    if (+addAge < 1) {
      setIsValid(false);
      return;
    }

    let user = {
      name: addUsername,
      age: addAge,
      id: Math.random()
    }

    props.onAddUser(user);

    setAddUsername("");
    setAddAge("");
  }

  function addUsernameHandler(event) {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setAddUsername(event.target.value);
  }

  function addAgeHandler(event) {
    if (event.target.value.trim().length > 0) {
      setIsValid(true);
    }
    setAddAge(event.target.value);
  }

  return (
    <Card className={classes.input}>
      <form onSubmit={addUserFormHandler}>
        <label htmlFor="username">Username</label>
        <input
          id="username"
          onChange={addUsernameHandler}
          value={addUsername}
        ></input>
        <label htmlFor="age">Age (Years)</label>
        <input
          id="age"
          onChange={addAgeHandler}
          value={addAge}
        ></input>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
}

export default AddUser;
